import React, { useState } from 'react';

import useDocumentScrollThrottled from './useDocumentScrollThrottled.js';

function Header(props) {
  const [shouldHideHeader, setShouldHideHeader] = useState(false);
  const [shouldShowShadow, setShouldShowShadow] = useState(false);

  const MINIMUM_SCROLL = 80;
  const TIMEOUT_DELAY = 400;

  useDocumentScrollThrottled(callbackData => {
    const { previousScrollTop, currentScrollTop } = callbackData;
    const isScrolledDown = previousScrollTop < currentScrollTop;
    const isMinimumScrolled = currentScrollTop > MINIMUM_SCROLL;

    setShouldShowShadow(currentScrollTop > 2);

    setTimeout(() => {
      setShouldHideHeader(isScrolledDown && isMinimumScrolled);
    }, TIMEOUT_DELAY);
  });

  const shadowStyle = shouldShowShadow ? 'shadow' : '';
  const hiddenStyle = shouldHideHeader ? 'hidden' : '';

  return (
    <header className={`header ${shadowStyle} ${hiddenStyle}`}>
      <div className="logo"><a href="">LMS</a></div>
      <div className="flexbox">
        <div className="search">
          <div>
            <input type="text" placeholder="       Search . . ." required></input>
          </div>
        </div>
      </div>
      <ul className="links">
        <Route path="/courses" render={(props) => (
          props.match ?
            <li className="link-item selected" id="my-courses"><a href="">Мои курсы</a></li> :
            <li className="link-item" id="my-courses"><a href="">Мои курсы</a></li>
        )}/>
        <Route path="/student-book" render={(props) => (
          props.match ?
            <li className="link-item selected" id="student-book"><a href="">Зачетка</a></li> :
            <li className="link-item" id="student-book"><a href="">Зачетка</a></li>
        )}/>
        <Route path="/schedule" render={(props) => (
          props.match ?
            <li className="link-item selected" id="schedule"><a href="">Расписание</a></li> :
            <li className="link-item" id="schedule"><a href="">Расписание</a></li>
        )}/>
        <Route path="/all-sections" render={(props) => (
          props.match ?
            <li className="link-item selected" id="all-sections"><a href="">Все разделы</a></li> :
            <li className="link-item" id="all-sections"><a href="">Все разделы</a></li>
        )}/>
        <Route path="/chat" render={(props) => (
          props.match ?
            <li className="link-item selected" id="chat"><a href=""><img src="chat.png"></img></a></li> :
            <li className="link-item" id="chat"><a href=""><img src="chat.png"></img></a></li>
        )}/>
        <Route path="/profile" render={(props) => (
          props.match ?
            <li className="link-item selected" id="profile"><a href=""><img src="profile.png"></img></a></li> :
            <li className="link-item" id="profile"><a href=""><img src="profile.png"></img></a></li>
        )}/>
        <Route path="/exit" render={(props) => (
          props.match ?
            <li className="link-item selected" id="exit"><a href="">Выйти</a></li> :
            <li className="link-item" id="exit"><a href="">Выйти</a></li>
        )}/>
      </ul>
    </header>
  );
}

export default Header;
